$(function() {
  
	    $('#apply_coupon').on('click', function(e) {
			
			if($('#apply_coupon').hasClass('used')){
				return false;
			}
			var coupon_code = $('#coupon_code').val();
			var planprice = $('#planprice').val();
			var custom = $('#custom').val();
			if(!coupon_code){
				$('#coupon_code').addClass('error');
				return false;
			}
		
			
			$('#coupon_code').removeClass('error');
			
			$.ajax({
				url: "ajax/applyCoupon.php",
				type: "POST",
				dataType: "json",
				data: 'coupon_code=' + coupon_code + '&planprice=' + planprice + '&custom=' + custom,
				success: function(data) {
					if(data.success){
						$('#apply_coupon').addClass("used");
						$('#planprice').val(data.planprice);
						$('#profileEmail').text(data.profileEmail);
						$('#coupon_error').text('');
					}else{
						$('#coupon_error').text(data.message);
					}
				}
			});
    });
 
})